//Get property uses getPropertyValue( "Key/Property" ) method. 
//https://www.soapui.org/docs/scripting-and-properties/tips-tricks/  

//Interface TestRunner: //https://www.soapui.org/apidocs/5.5.0/com/eviware/soapui/model/testsuite/TestRunner.html   

// GET a project property 

def projectProperty = testRunner.testCase.testSuite.project.getPropertyValue( "MyProp" ) 
//Print the projectProperty
log.info("projectProperty is: "+projectProperty);  

// Get a test suite property 
def testSuiteProperty = testRunner.testCase.testSuite.getPropertyValue( "MyProp" ) 
log.info("testSuiteProperty is:" +testSuiteProperty);  

// Get a test case property 
def testCaseProperty = testRunner.testCase.getPropertyValue("MyProp") 
log.info("testCaseProperty is:" +testCaseProperty);  

// Get a test steps property 
def testStepProperty = testRunner.testCase.getTestStepByName("propertiesFor_TestSpeps").getPropertyValue("MyProp") 
log.info("testStepProperty is:" +testStepProperty);  

// Get a global property 
def globalProperty = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue( "MyProp" ) 
log.info("globalProperty is:" + globalProperty);   

//SET PROPERTY  
// Set a project property 
testRunner.testCase.testSuite.project.setPropertyValue( "MyProp", "someValue project property" )   

// Set a test suite property 
testRunner.testCase.testSuite.setPropertyValue( "MyProp", "someValue in TestSuite property" )

// Set a test case property 
testRunner.testCase.setPropertyValue( "MyProp", "someValue in TestCase property" )  

// Set a test step property 
testRunner.testCase.getTestStepByName("propertiesFor_TestSpeps").setPropertyValue( "MyProp", "someValue in TestSteps property" )   

// Set a global property
com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "MyProp", "someValue in Global property" )

